mapper.select:
SELECT NOTICESEQ, MEMBERID, NOTICE_TITLE, NOTICE_CONTENT, NOTICE_FILENAME, NOTICE_NEWFILENAME, READCOUNT, DOWNCOUNT, REGDATE,NOTICE_REF FROM (SELECT ROW_NUMBER()OVER(ORDER BY NOTICE_REF DESC) AS RNUM, NOTICESEQ, MEMBERID, NOTICE_TITLE, NOTICE_CONTENT, NOTICE_FILENAME,NOTICE_NEWFILENAME, READCOUNT, DOWNCOUNT,REGDATE, NOTICE_REF FROM NOTICE WHERE 1=1
AND NOTICE_TITLE LIKE '%'||#{search}||'%'
AND NOTICE_CONTENT LIKE '%'||#{search}||'%'
AND MEMBERID=#{search}
ORDER BY NOTICE_REF DESC) WHERE RNUM BETWEEN ${start} AND ${end}
