mapper.select:
SELECT RESUMESEQ
FROM RESUME
WHERE RESUMETITLE = #{resumetitle}

SELECT RESUMESEQ, MEMBERID, RESUMETITLE, RECRUIT_STATUS, RESUME_STATUS, RESUMEIMAGE, NEWRESUMEIMAGE, RESUME_INTRO_TITLE, RESUME_INTRO_CONTENT, PORTFOLIO, NEWPORTFOLIO, DESIREDSALARY, DESIREDJOBTYPE, DESIRED_STR, DESIRED_END, DESIREDAREA1, DESIREDJOB1, GENDER
FROM RESUME
WHERE RESUMESEQ=#{seq}

SELECT CAREERSEQ, RESUMESEQ, PRE_COMNAME, PRE_STARTDATE, PRE_ENDDATE, PRE_STATUS, PRE_POSITION, PRE_BUSCODE, PRE_AREA, PRE_DEPT, PRE_SAL, PRE_JOBDETAIL, PRE_REASON
FROM CAREER
WHERE RESUMESEQ=#{seq}

SELECT LICSEQ, RESUMESEQ, LIC_DATE, LIC_NAME, LIC_PUBLISHER, LIC_PASS
FROM LICENSE
WHERE RESUMESEQ=#{seq}

SELECT ACTIVITYSEQ, RESUMESEQ, ACT_FIELD, ACT_ORG, ACT_STR, ACT_END, ACT_DETAIL
FROM ACTIVITY
WHERE RESUMESEQ=#{seq}

SELECT AWDSEQ, RESUMESEQ, AWD_NAME, AWD_DATE, AWD_ORG
FROM AWARD
WHERE RESUMESEQ=#{seq}

SELECT LANSEQ, RESUMESEQ, LAN_KIND, LAN_EXAM, LAN_SCORE, LAN_GRADE, LAN_PASS, LAN_DATE
FROM LANGUAGE
WHERE RESUMESEQ=#{seq}

mapper.update:
UPDATE RESUME
SET PROGRESS = 10
WHERE RESUMESEQ=#{resumeseq}

UPDATE RESUME
SET PROGRESS = PROGRESS+10
WHERE RESUMESEQ=#{resumeseq}

UPDATE RESUME
SET RESUMETITLE=#{resumetitle}, RECRUIT_STATUS=#{recruit_status}, RESUME_STATUS=#{resume_status}, RESUMEIMAGE=#{resumeimage}, NEWRESUMEIMAGE=#{newresumeimage},
            RESUME_INTRO_TITLE=#{resume_intro_title}, RESUME_INTRO_CONTENT=#{resume_intro_content}, DESIREDSALARY=#{desiredsalary},
            DESIREDJOBTYPE=#{desiredjobtype}, DESIRED_STR=#{desired_str}, DESIRED_END=#{desired_end}, DESIREDAREA1=#{desiredarea1}, DESIREDJOB1=#{desiredjob1}, GENDER=#{gender}
WHERE RESUMESEQ=#{resumeseq}

UPDATE RESUME_EDU
SET HIGHSCHOOL=#{highschool}, HIGH_STR=#{high_str}, HIGH_STR_STATUS=#{high_str_status}, HIGH_END=#{high_end}, HIGH_END_STATUS=#{high_end_status}, UNIVERSITY=#{university}, UNIV_STATUS=#{univ_status}, UNIV_STR=#{univ_str}, UNIV_STR_STATUS=#{univ_str_status},
      UNIV_END=#{univ_end}, UNIV_END_STATUS=#{univ_end_status}, UNIV_MAJOR_FIELD=#{univ_major_field}, UNIV_MAJOR=#{univ_major}, UNIV_NIGHT=#{univ_night}, UNIV_GRADE=#{univ_grade}, UNIV_GRADE_BASE=#{univ_grade_base}, UNIV_PAPER=#{univ_paper}
WHERE RESUMESEQ=#{resumeseq}

	UPDATE CAREER
    SET PRE_COMNAME=#{pre_comname}, PRE_STARTDATE=#{pre_startdate}, PRE_ENDDATE=#{pre_enddate}, PRE_STATUS=#{pre_status}, PRE_POSITION=#{pre_position},
    PRE_BUSCODE=#{pre_buscode}, PRE_AREA=#{pre_area}, PRE_DEPT=#{pre_dept}, PRE_SAL=#{pre_sal}, PRE_JOBDETAIL=#{pre_jobdetail}, PRE_REASON=#{pre_reason}
WHERE RESUMESEQ=#{resumeseq}

   UPDATE LICENSE
    SET LIC_DATE=#{lic_date}, LIC_NAME=#{lic_name}, LIC_PUBLISHER=#{lic_publisher}, LIC_PASS=#{lic_pass}
WHERE RESUMESEQ=#{resumeseq}

   UPDATE ACTIVITY
    SET ACT_FIELD=#{act_field}, ACT_ORG=#{act_org}, ACT_STR=#{act_str}, ACT_END=#{act_end}, ACT_DETAIL=#{act_detail}
WHERE RESUMESEQ=#{resumeseq}

   UPDATE AWARD
    SET AWD_NAME=#{awd_name}, AWD_DATE=#{awd_date}, AWD_ORG=#{awd_org}
WHERE RESUMESEQ=#{resumeseq}

   UPDATE LANGUAGE
    SET LAN_KIND=#{lan_kind}, LAN_EXAM=#{lan_exam}, LAN_SCORE=#{lan_score}, LAN_GRADE=#{lan_grade}, LAN_PASS=#{lan_pass}, LAN_DATE=#{lan_date}
WHERE RESUMESEQ=#{resumeseq}

mapper.select:
SELECT JOBTITLE
FROM JOB
WHERE JOBSEQ=#{jobseq}

SELECT RESUMETITLE
FROM RESUME
WHERE RESUMESEQ=#{resumeseq}

mapper.update:
UPDATE HIGHSCHOOL
SET HIGHSCHOOL=#{highschool}, HIGH_STR=#{high_str}, HIGH_STR_STATUS=#{high_str_status}, HIGH_END=#{high_end}, HIGH_END_STATUS=#{high_end_status}
WHERE RESUMESEQ=#{resumeseq}

UPDATE UNIVERSITY
SET UNIVERSITY=#{university}, UNIV_STATUS=#{univ_status}, UNIV_STR=#{univ_str}, UNIV_STR_STATUS=#{univ_str_status},
      UNIV_END=#{univ_end}, UNIV_END_STATUS=#{univ_end_status}, UNIV_MAJOR_FIELD=#{univ_major_field}, UNIV_MAJOR=#{univ_major}, UNIV_NIGHT=#{univ_night}, UNIV_GRADE=#{univ_grade}, UNIV_GRADE_BASE=#{univ_grade_base}, UNIV_PAPER=#{univ_paper}
WHERE RESUMESEQ=#{resumeseq}

mapper.select:
SELECT HIGHSCHOOLSEQ, RESUMESEQ, HIGHSCHOOL, HIGH_STR, HIGH_STR_STATUS, HIGH_END, HIGH_END_STATUS
FROM HIGHSCHOOL
WHERE RESUMESEQ=#{seq}
